import React, {useContext, useState, useEffect} from 'react'
import {Switch, Route} from 'react-router-dom'
import Login from './auth/Login'
import Detailproduct from './detailProduct/DetailProduct'
import Register from './auth/Register'
import Cart from './cart/Cart'
import Products from './products/Products'
import NotFound from './utils/not_found/NotFound'
import {GlobalState} from '../../GlobalState'
import HashLoader from "react-spinners/HashLoader";


function Pages(){

    const state = useContext(GlobalState)
    const [isLogged] = state.userAPI.isLogged

    const [loading, setLoading] = useState(false);
    //const [color, setColor] = useState("#157A3F");
    const [color, setColor] = useState("#FFFFFF");
  
    useEffect(() => {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
      }, 4000);
    }, [])

    return (
        
        <div>
            {
          loading ? 
          <div className="container center-block-message">
            <h1 className="page-title-loading">ArvaZon is loading...</h1>
          <HashLoader color={color} loading={loading} size={150} /> 
          </div> :
        <>  
        <Switch>
        <Route path="/" exact component={Products} />
            <Route path="/detail/:id" exact component={Detailproduct} />
            <Route path="/login" exact component={isLogged ? NotFound : Login} />
            <Route path="/register" exact component={isLogged ? NotFound : Register} />
            <Route path="/cart" exact component={isLogged ? Cart : Products} />
            <Route path="*" exact component={NotFound} />
        </Switch>
            
          </>
        }
        </div>    
    )
}

export default Pages;
