import React, {useContext, useState, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import {Link} from 'react-router-dom'
import axios from 'axios';
import StripeContainer from './StripeContainer'
import swal from 'sweetalert';

function Cart() {

    const state = useContext(GlobalState)
    const [cart, setCart] = state.userAPI.cart;
    const [token] = state.token;
    const [total,setTotal] = useState(0)
    const [btnDisabled, setDisabled] = useState(false)
    const [showItem, setShowItem] = useState(false)

    useEffect(() => {

        const getTotal = () => {
            const total = cart.reduce((prev,item) => {
                return prev + (item.price * item.quantity)
            }, 0)

            setTotal(total)
            total === 0 ? setDisabled(true) : setDisabled(false)
        }

        getTotal()
    }, [cart])

    const addToCart = async () => {
        await axios.patch('/user/addcart', {cart}, {
            headers: {Authorization: token}
        })
    }

    const increment = (id) => {
        cart.forEach(item => {
            if(item._id === id){
                item.quantity === 10 ? item.quantity = 10 : item.quantity += 1;
            }

        })

        setCart([...cart])
        addToCart()
    }

    const decrement = (id) => {
        cart.forEach(item => {
            if(item._id === id){
                item.quantity === 1 ? item.quantity = 1 : item.quantity -= 1;
            }
        })

        setCart([...cart])
        addToCart()
    }

    const removeProduct = (id) => {
        if(swal({
            title: "Message from ArvaZon!",
            text: "Are you sure you want to remove this product from the cart?",
            icon: "warning",
            buttons: true,
            dangerMode: true,
          })
          .then((willDelete) => {
            if (willDelete) {
              swal("Product successfully removed from the cart!", {
                icon: "success",
              });
              cart.forEach((item, index) =>{
                if(item._id === id){
                    cart.splice(index, 1)
                }
            })
            setCart([...cart])
            addToCart()
            }
          })){
            

            
        }
    }

    {
        total === 0 ? swal({
            title: "Message from ArvaZon!",
            text: "The cart is empty!",
            icon: "warning",
            button: true,
          })
       : <></>
    }

        
    return (

        <div className="container">
                <div className="detail">
                <h2 className="page-title text-center">Shopping Cart</h2>
            {
                showItem ? <StripeContainer /> : 
                cart.map(product => (
                    <div className="row detail-cols p50 mb20" key={product._id}>
                    <div className="col-lg-6 mt20 mb20">
                    <img src={product.images.url} alt="product image" />
                    </div>
                    <div className="col-lg-6 mt20 mb20">
                        <h1 className="detail-title">{product.title}</h1>
                        <h3 className="detail-price">€{product.price * product.quantity}</h3>
                        <p className="detail-description"><b>Descrizione: </b>{product.description}</p>
                        <p className="detail-category"><b>Categoria:</b> {product.category}</p>
                        <div className="amount">
                            <button onClick={() => decrement(product._id)}> - </button>
                            <span>{product.quantity}</span>
                            <button onClick={() => increment(product._id)}> + </button>
                            <button className="delete" onClick={() => removeProduct(product._id)}>Remove</button>
                        </div>

                    </div>
                </div>
                ))
                
            }

{
    !showItem ? <div className="total p50 mb20">
    <h3 className="price-total">Total: €{total}</h3>
    <Link className="btn_pay" to="#!" style={btnDisabled ? "display: none" : "display: block"} onClick={() => setShowItem(true)}>Pay</Link>
</div> : null

}
        
        </div>
        </div>
    )
}

export default Cart
