import React, {useContext} from 'react'
import {GlobalState} from '../../../GlobalState'

function Cart() {

    const state = useContext(GlobalState)
    const [cart] = state.userAPI.cart;

    if(cart.length === 0) 
        return <h2 className="text-center">Cart Empty</h2>

    return (
        <div className="container">
                <div className="detail">
            {
                <div className="row detail-cols p50">
                    <div className="col-lg-6 mt20 mb20">
                    <img src={detailProduct.images.url} alt="product image" />
                    </div>
                    <div className="col-lg-6 mt20 mb20">
                        <h1 className="detail-title">{detailProduct.title}</h1>
                        <span className="detail-price">â‚¬{detailProduct.price}</span>
                        <p className="detail-description"><b>Descrizione: </b>{detailProduct.description}</p>
                        <p className="detail-category"><b>Categoria:</b> {detailProduct.category}</p>
                        <p className="detail-sold"><b>Pezzi Venduti:</b> {detailProduct.sold}</p>
                        <Link to="/cart" className="cart">Compra</Link>
                    </div>
                </div>
            }
        </div>
        </div>
    )
}

export default Cart
