import React, {useState, useContext} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../GlobalState'
import swal from 'sweetalert';
import HashLoader from "react-spinners/HashLoader";

const initialState = {
    product_id: '',
    title: '',
    price: 0,
    description: '',
    content: '',
    category: '',


}


function CreateProduct() {

    const state = useContext(GlobalState)
    const [product, setProduct] = useState(initialState)
    const [categories] = state.categoriesAPI.categories
    const [images, setImages] = useState(false)
    const [isAdmin] = state.userAPI.isAdmin
    const [token] = state.token
    const [loading, setLoading] = useState(false);
    const [color, setColor] = useState("#157A3F");

    const handleUpload = async e => {
        e.preventDefault();
        try {
            if(!isAdmin) return (swal({
                title: "Message from ArvaZon!",
                text: "You're not an Admin!",
                icon: "warning",
                button: true,
              }))

              const file = e.target.files[0]
              
              if(!file) return (swal({
                title: "Message from ArvaZon!",
                text: "Files does not exists!",
                icon: "warning",
                button: true,
              }))

              if(file.size > 1024 * 1024) // 1mb
              return (swal({
                title: "Message from ArvaZon!",
                text: "File size too large!",
                icon: "warning",
                button: true,
              }))

              if(file.type !== 'image/jpeg' && file.type !== 'image/png') // 1mb
              return (swal({
                title: "Message from ArvaZon!",
                text: "Invalid file format!",
                icon: "warning",
                button: true,
              }))

              let formData = new FormData()
              formData.append('file', file)

              const res = await axios.post('/api/upload', formData, {
                  headers: {
                    'content-type': 'multipart/form-data',  
                    Authorization: token}
              })

              setImages(res.data)
              
        

            
        } catch (err) {
            swal({
                title: "Message from ArvaZon!",
                text: err.response.data.msg,
                icon: "warning",
                button: true,
              })
        }
    }

    const styleUpload = {
        display: images ? "block": "none"
    }

    return (
        <>
        <div className="container mt-100">
        <h2 className="page-title mt-200">Manage Product</h2>

            <form className="bg-white p-50 border-radius">
            <label htmlFor="file">Image</label>
            <div className="upload">
                <input className="form-control" type="file" name="file" id="file_up" onChange={handleUpload} />
                
                {
                    loading ? <div id="file_img">
                        <HashLoader color={color} loading={loading} size={150} />
                    </div>
                     : 
                    <div id="file_img" style={styleUpload}>
                        <img src={images ? images.url : ''} />
                        <span>X</span>
                    </div>
                }
                
            </div>

               <div className="form-group">
                  <label htmlFor="product_id">Product ID</label>
                  <input className="form-control" type="text" name="product_id" id="product_id" required value={product.product_id} />
               </div> 

               <div className="form-group">
                  <label htmlFor="title">Title</label>
                  <input className="form-control" type="text" name="title" id="title" required value={product.title} />
               </div> 

               <div className="form-group">
                  <label htmlFor="price">Price</label>
                  <input className="form-control" type="number" name="price" id="price" required value={product.price} />
               </div> 

               <div className="form-group">
                  <label htmlFor="description">Description</label>
                  <textarea className="form-control" type="text" name="description" id="description" required value={product.description} rows="5" />
               </div> 

               <div className="form-group">
                  <label htmlFor="content">Content</label>
                  <textarea className="form-control" type="text" name="content" id="content" required value={product.content} rows="7" />
               </div> 

               <div className="form-group">
                  <label htmlFor="category">Category</label>
                  <select className="form-control" name="category" value={product.category} required>
                      {
                          categories.map(category => (
                              <option value={category._id} key={category._id}>
                                  {category.name}
                              </option>
                          ))
                      }
                  </select>
               </div> 

                <div className="form-group text-center">
                      <button className="form_btn" type="submit">Create Product</button>
                </div>

            </form>

        </div>
        </>
    )
}

export default CreateProduct
