import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import ProductItem from '../utils/productItem/ProductItem'

function DetailProduct() {

    const params = useParams()
    const state = useContext(GlobalState)
    const [products] = state.productsAPI.products
    const [detailProduct, setDetailProduct] = useState([])

    useEffect(() => {
        if(params.id){
            products.forEach(product => {
                if(product._id === params.id) setDetailProduct(product)
            })
        }
    }, [params.id,products])

    if(detailProduct.length === 0) return null;

    return (
        
            <div className="container">
                <div className="detail">
                <div className="row  detail-cols p50">
                    <div className="col-lg-6 mt20 mb20">
                    <img src={detailProduct.images.url} alt="product image" />
                    </div>
                    <div className="col-lg-6 mt20 mb20">
                        <h1 className="detail-title">{detailProduct.title}</h1>
                        <span className="detail-price">€{detailProduct.price}</span>
                        <p className="detail-description"><b>Descrizione: </b>{detailProduct.description}</p>
                        <p className="detail-category"><b>Categoria:</b> {detailProduct.category}</p>
                        <p className="detail-sold"><b>Pezzi Venduti:</b> {detailProduct.sold}</p>
                        <Link to="/cart" className="cart">Compra</Link>
                    </div>
                </div>

                <div>
                    
                    <div className="products text-center p50">
                    <h2 className="title mt20 mb20">Prodotti Correlati</h2>
                        <div className="container">
                            <div className="row">
                            {
                            products.map(product => {
                                return product.category === detailProduct.category
                                ? <ProductItem key={product._id} product={product} /> : null
                            })
                        }
                            </div>
                        </div>
                        
                    </div>
                </div>
            </div>
            
        </div>
    )
}

export default DetailProduct
