import React, {useContext} from 'react'
import {Link} from 'react-router-dom'
import {GlobalState} from '../../../../GlobalState'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons'
import { faInfoCircle } from '@fortawesome/free-solid-svg-icons'
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons'
import { faEdit } from '@fortawesome/free-solid-svg-icons'
import swal from 'sweetalert';
import axios from 'axios';
import HashLoader from "react-spinners/HashLoader";




function ProductItem({product, isAdmin, token, callback, setCallback}) {

    const state = useContext(GlobalState)
    const addCart = state.userAPI.addCart

    const deleteProduct = async () => {
        console.log(product);
        try {
            const destroyImg = await axios.post('/api/destroy', {public_id: product.images.public_id}, { 
                headers: {Authorization: token}
            })
            const deleteProduct = await axios.delete(`/api/products/${product._id}`, { 
                headers: {Authorization: token}
            })

            await destroyImg
            await deleteProduct
            swal({
                title: "Message from ArvaZon!",
                text: "Product deleted successfully!!", 
                icon: "warning",
                button: true
              })
            setCallback(!callback)

        } catch (err) {
            swal({
                title: "Message from ArvaZon!",
                text: err.response.data.msg, 
                icon: "warning",
                button: true
              }) 
        }
    }

    const handleCheck = () => {
        product.checked = !product.checked;
    }

    return (
        <div className="col-lg-4 col-md-6 col-sm-12">
            <div className="product-card">
                {
                    isAdmin && <input type="checkbox" checked={product.checked} onChange={handleCheck} />
                }
                <img src={product.images.url} alt="" />
                <div className="product-box">
                    <h2 title={product.title}>{product.title}</h2>
                    <span className="price"><b>€{product.price}</b></span>
                </div>
                <div className="row_btn">
                    {
                        isAdmin ? 
                        <>
                        <Link id="btn_view" to="#!">
                        <FontAwesomeIcon icon={faTrashAlt}  size="2x" onClick={deleteProduct} />
                    </Link>
                    <Link id="btn_buy" to={`/edit_product/${product._id}`}>
                        <FontAwesomeIcon icon={faEdit}  size="2x" />
                    </Link>
                        </>
                        :
                        <>
                        <Link id="btn_buy" to="/cart" onClick={ () => addCart(product) }>
                        <FontAwesomeIcon icon={faShoppingCart}  size="2x" /> 
                    </Link>
                    <Link id="btn_view" to={`/detail/${product._id}`}>
                        <FontAwesomeIcon icon={faInfoCircle}  size="2x" /> 
                    </Link>
                        </>
                    }
                    
                </div>
            </div>
        </div>
    )
}

export default ProductItem
