import React, {useState, useContext} from 'react'
import {GlobalState} from '../../../GlobalState'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons'
import { faEdit } from '@fortawesome/free-solid-svg-icons'
import swal from 'sweetalert';
import axios from 'axios'

function Categories() {

    const state = useContext(GlobalState)
    const [categories, setCategories] = state.categoriesAPI.categories
    const [category, setCategory] = useState('')
    const [token] = state.token
    const [callback, setCallback] = state.categoriesAPI.callback
    const [onEdit, setOnEdit] = useState(false)

    const createCategory = async e => {
        e.preventDefault();
        try{
            const res = await axios.post('/api/category', {name: category}, {
                headers: {Authorization: token}
            })
            setCallback(!callback)
            setCategory('')
            swal({
                title: "Message from ArvaZon!",
                text: "Category created!",
                icon: "success",
                button: true,
              })
        }catch(err){
            swal({
                title: "Message from ArvaZon!",
                text: err.response.data.msg,
                icon: "warning",
                button: true,
              })
        }
    }

    const editCategory = async (name,id) => {

    }


    return (
        <>
        <div className="container mt-100">
        <h2 className="page-title mt-200">Manage Categories</h2>
            <form className="bg-white p-50 border-radius" onSubmit={createCategory}>
            <h2 className="mt20 text-center">Add Category</h2>
                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <input className="form-control" type="text" name="category" value={category} required onChange={e => setCategory(e.target.value)} />
                </div>
                    <div className="form-group text-center">
                    <button className="form_btn" type="submit">Add Category</button>
                    </div>
            </form>
        </div>

        <div className="container bg-white mt-100 text-center p-50 border-radius">
            <h2 className="mt20">Categories List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Category Name</th>
                        <th>Edit Category</th>
                        <th>Delete Category</th>
                    </tr>
                </thead>
                <tbody>
                {
                categories.map(category =>(
                   <tr key={category._id}>
                       <td>{category.name}</td>
                       <td>
                       <button className="btn-edit"><FontAwesomeIcon icon={faEdit}  size="2x" title="Edit category" /></button>
                       </td>
                       <td>
                       <button className="btn-delete"><FontAwesomeIcon icon={faTrashAlt}  size="2x" title="Delete category" /></button>
                       </td>
                   </tr> 

                ))
            }
                </tbody>
            </table>
        </div>
        </>
    )
}

export default Categories
