import React, {useContext, useState, useEffect} from 'react'
import {GlobalState} from '../../../GlobalState'
import {Link} from 'react-router-dom'
import axios from 'axios';

function Cart() {

    const state = useContext(GlobalState)
    const [cart, setCart] = state.userAPI.cart;
    const [token] = state.token;
    const [total,setTotal] = useState(0)
    const [showItem, setShowItem] = useState(false)

    useEffect(() => {

        const getTotal = () => {
            const total = cart.reduce((prev,item) => {
                return prev + (item.price * item.quantity)
            }, 0)

            setTotal(total)
        }

        getTotal()
    }, [cart])

    const addToCart = async () => {
        await axios.patch('/user/addcart', {cart}, {
            headers: {Authorization: token}
        })
    }

    const increment = (id) => {
        cart.forEach(item => {
            if(item._id === id){
                item.quantity === 10 ? item.quantity = 10 : item.quantity += 1;
            }

        })

        setCart([...cart])
        addToCart()
    }

    const decrement = (id) => {
        cart.forEach(item => {
            if(item._id === id){
                item.quantity === 1 ? item.quantity = 1 : item.quantity -= 1;
            }
        })

        setCart([...cart])
        addToCart()
    }

    const removeProduct = (id) => {
        if(window.confirm("Do you want to remove this product from the Cart?")){
            cart.forEach((item, index) =>{
                if(item._id === id){
                    cart.splice(index, 1)
                }
            })

            setCart([...cart])
            addToCart()
        }
    }


    if(cart.length === 0) 
        return <h2 className="text-center">Cart Empty</h2>

    return (
        <div className="container">
                <div className="detail">
            {
                cart.map(product => (
                    <div className="row detail-cols p50 mb20" key={product._id}>
                    <div className="col-lg-6 mt20 mb20">
                    <img src={product.images.url} alt="product image" />
                    </div>
                    <div className="col-lg-6 mt20 mb20">
                        <h1 className="detail-title">{product.title}</h1>
                        <h3 className="detail-price">€{product.price * product.quantity}</h3>
                        <p className="detail-description"><b>Descrizione: </b>{product.description}</p>
                        <p className="detail-category"><b>Categoria:</b> {product.category}</p>
                        <div className="amount">
                            <button onClick={() => decrement(product._id)}> - </button>
                            <span>{product.quantity}</span>
                            <button onClick={() => increment(product._id)}> + </button>
                            <button className="delete" onClick={() => removeProduct(product._id)}>Remove</button>
                        </div>

                    </div>
                </div>
                ))
                
            }

            <div className="total p50 mb20">
                <h3>Total: € {total}</h3>
                <Link className="btn_pay" to="#!">Pay</Link>
            </div>

        </div>
        </div>
    )
}

export default Cart
