import React, {useContext} from 'react'
import {GlobalState} from '../../../GlobalState'

function Cart() {

    const state = useContext(GlobalState)
    const [cart] = state.userAPI.cart;

    if(cart.length === 0) 
        return <h2 className="text-center">Cart Empty</h2>

    return (
        <div className="container">
                <div className="detail">
            {
                cart.map(product => (
                    <div className="row detail-cols p50 mb20">
                    <div className="col-lg-6 mt20 mb20">
                    <img src={product.images.url} alt="product image" />
                    </div>
                    <div className="col-lg-6 mt20 mb20">
                        <h1 className="detail-title">{product.title}</h1>
                        <span className="detail-price">â‚¬{product.price * product.quantity}</span>
                        <p className="detail-description"><b>Descrizione: </b>{product.description}</p>
                        <p className="detail-category"><b>Categoria:</b> {product.category}</p>
                        <p className="detail-sold"><b>Pezzi Venduti:</b> {product.sold}</p>
                    </div>
                </div>
                ))
                
            }
        </div>
        </div>
    )
}

export default Cart
